@/registry/examples/slider-demo/slider-r.content:
"use client"

import { Field, FieldDescription, FieldTitle } from "@/components/ui/field"
import { Slider } from "@/components/ui/slider"
import type React from "react"
import { useState } from "react"

export default function SliderRangeDemo(): React.ReactElement {
    const [value, setValue] = useState<number[]>([20, 80])

    const handleValueChange = (newValue: number[]): void => {
        setValue(newValue)
    }

    return (
        <div className="w-full max-w-md">
            <Field>
                <FieldTitle>Performance Settings</FieldTitle>
                <FieldDescription>
                    Adjust your system performance (<span className="font-semibold text-foreground">{value[0]}%</span>
                    {" - "}
                    <span className="font-semibold text-foreground">{value[1]}%</span>)
                </FieldDescription>
                <div className="mt-6 space-y-4">
                    <Slider
                        value={value}
                        onValueChange={handleValueChange}
                        max={100}
                        min={0}
                        step={1}
                        className="w-full"
                        aria-label="Performance Range"
                    />
                    <div className="flex justify-between text-xs text-muted-foreground">
                        <span>{value[0]}%</span>
                        <span>{value[1]}%</span>
                    </div>
                </div>
            </Field>
        </div>
    )
}

@/registry/examples/smart-select-demo/sm.content:
"use client"

import * as React from "react"
import { Check, Search } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import {
    Command,
    CommandEmpty,
    CommandGroup,
    CommandInput,
    CommandItem,
    CommandList,
} from "@/components/ui/command"
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from "@/components/ui/popover"

const programmingLanguages = [
    {
        value: "javascript",
        label: "JavaScript",
    },
    {
        value: "typescript",
        label: "TypeScript",
    },
    {
        value: "python",
        label: "Python",
    },
    {
        value: "java",
        label: "Java",
    },
    {
        value: "go",
        label: "Go",
    },
    {
        value: "rust",
        label: "Rust",
    },
    {
        value: "php",
        label: "PHP",
    },
]

export default function SmartSelectDemo() {
    const [open, setOpen] = React.useState(false)
    const [selectedLanguage, setSelectedLanguage] = React.useState("")

    return (
        <Popover open={open} onOpenChange={setOpen}>
            <PopoverTrigger asChild>
                <Button
                    variant="outline"
                    role="combobox"
                    aria-expanded={open}
                    className="w-[240px] justify-between"
                >
                    {selectedLanguage
                        ? programmingLanguages.find((lang) => lang.value === selectedLanguage)?.label
                        : "اختر لغة البرمجة..."}
                    <Search className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                </Button>
            </PopoverTrigger>
            <PopoverContent className="w-[240px] p-0">
                <Command>
                    <CommandInput placeholder="ابحث عن لغة..." className="h-9" />
                    <CommandList>
                        <CommandEmpty>لا توجد لغة بهذا الاسم.</CommandEmpty>
                        <CommandGroup>
                            {programmingLanguages.map((language) => (
                                <CommandItem
                                    key={language.value}
                                    value={language.value}
                                    onSelect={(currentValue) => {
                                        setSelectedLanguage(currentValue === selectedLanguage ? "" : currentValue)
                                        setOpen(false)
                                    }}
                                >
                                    {language.label}
                                    <Check
                                        className={cn(
                                            "ml-auto h-4 w-4",
                                            selectedLanguage === language.value ? "opacity-100" : "opacity-0"
                                        )}
                                    />
                                </CommandItem>
                            ))}
                        </CommandGroup>
                    </CommandList>
                </Command>
            </PopoverContent>
        </Popover>
    )
}

"use client"

import * as React from "react"
import { Button } from "@/components/ui/button"
import {
    Command,
    CommandEmpty,
    CommandGroup,
    CommandInput,
    CommandItem,
    CommandList,
} from "@/components/ui/command"
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from "@/components/ui/popover"
import { Flag } from "lucide-react"

type Priority = {
    value: string
    label: string
    color: string
}

const priorities: Priority[] = [
    {
        value: "low",
        label: "منخفضة",
        color: "bg-blue-500",
    },
    {
        value: "medium",
        label: "متوسطة",
        color: "bg-yellow-500",
    },
    {
        value: "high",
        label: "عالية",
        color: "bg-orange-500",
    },
    {
        value: "urgent",
        label: "عاجلة",
        color: "bg-red-500",
    },
]

export default function SmartSelectPopoverDemo() {
    const [open, setOpen] = React.useState(false)
    const [selectedPriority, setSelectedPriority] = React.useState<Priority | null>(
        null
    )

    return (
        <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
                <Flag className="h-4 w-4 text-muted-foreground" />
                <span className="text-sm text-muted-foreground">الأولوية</span>
            </div>
            <Popover open={open} onOpenChange={setOpen}>
                <PopoverTrigger asChild>
                    <Button variant="outline" className="w-[180px] justify-start gap-2">
                        {selectedPriority ? (
                            <>
                                <span className={`h-2 w-2 rounded-full ${selectedPriority.color}`} />
                                {selectedPriority.label}
                            </>
                        ) : (
                            <>+ تحديد الأولوية</>
                        )}
                    </Button>
                </PopoverTrigger>
                <PopoverContent className="p-0" side="right" align="start">
                    <Command>
                        <CommandInput placeholder="ابحث عن الأولوية..." />
                        <CommandList>
                            <CommandEmpty>لا توجد نتائج.</CommandEmpty>
                            <CommandGroup>
                                {priorities.map((priority) => (
                                    <CommandItem
                                        key={priority.value}
                                        value={priority.value}
                                        onSelect={(value) => {
                                            setSelectedPriority(
                                                priorities.find((p) => p.value === value) ||
                                                null
                                            )
                                            setOpen(false)
                                        }}
                                    >
                                        <span className={`mr-2 h-2 w-2 rounded-full ${priority.color}`} />
                                        {priority.label}
                                    </CommandItem>
                                ))}
                            </CommandGroup>
                        </CommandList>
                    </Command>
                </PopoverContent>
            </Popover>
        </div>
    )
}

