import React from "react"

import { formatPrice } from "../lib/utils"

interface DiscountPriceProps {
  price: number
  discount: number
  quantity?: number
}
const DiscountPrice: React.FC<DiscountPriceProps> = ({
  price,
  discount,
  quantity = 1,
}) => {
  // here we are using Math.ceil to round up the price to the nearest integer
  // this is because the price is in EGP and we want to avoid decimal values
  let priceIncreasing = Math.ceil(price)
  const discountedPrice = Math.ceil(priceIncreasing * (1 - discount))
  // When using the quantity parameter, please ensure that the shipping price is updated accordingly, as shipping costs depend on the quantity of products ordered.
  // const PriceAfterTimesQuantity = discountedPrice * quantity + shippingPrice + taxPrice * (discountedPrice * quantity);
  const formattedPrice = formatPrice(discountedPrice * quantity)
  return (
    <div>
      <p className="text-destructive font-semibold">{formattedPrice}</p>
    </div>
  )
}

export default DiscountPrice

import React from "react"

import { formatPrice } from "../lib/utils"

type ProductPrices = {
  price: number
  quantity?: number
}

const NormalPrice: React.FC<ProductPrices> = ({ price, quantity = 1 }) => {
  // here we are using Math.ceil to round up the price to the nearest integer
  // this is because the price is in EGP and we want to avoid decimal values
  const PriceAfterTimesQuantity = price * quantity
  let normalPrice = Math.ceil(PriceAfterTimesQuantity)
  // If you want to add more parameters to the price like: shippingPrice, taxPrice, etc.
  // you can add it like this:
  // When using the quantity parameter, please ensure that the shipping price is updated accordingly, as shipping costs depend on the quantity of products ordered.
  // const PriceAfterTimesQuantity = normalPrice * quantity + shippingPrice + taxPrice * (normalPrice * quantity);
  const formattedTotalPrice = formatPrice(normalPrice)
  return (
    <div>
      <p>{formattedTotalPrice}</p>
    </div>
  )
}

export default NormalPrice

"use client"

import React, { useEffect, useState } from "react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

export type ProductColorTemp = "warm" | "cool" | "white"

export interface ColorTemperatureOption {
  value: ProductColorTemp
  label: string
  icon?: React.ReactNode
}

const DEFAULT_OPTIONS: ColorTemperatureOption[] = [
  { value: "warm", label: "Warm" },
  { value: "cool", label: "Cool" },
  { value: "white", label: "White" },
]

interface ColorTemperatureSelectorProps {
  value: ProductColorTemp
  onValueChange: (newValue: ProductColorTemp) => void
  options?: ColorTemperatureOption[]
  title?: string
  disabled?: boolean
  className?: string
  buttonClassName?: string
}

export function ColorTemperatureSelector({
  value,
  onValueChange,
  options = DEFAULT_OPTIONS,
  title = "Color Temperature",
  disabled = false,
  className,
  buttonClassName,
}: ColorTemperatureSelectorProps) {
  const [selectedValue, setSelectedValue] = useState<ProductColorTemp>(value)

  useEffect(() => {
    setSelectedValue(value)
  }, [value])

  const handleSelectionChange = (newValue: ProductColorTemp) => {
    if (disabled || newValue === selectedValue) return

    setSelectedValue(newValue)
    onValueChange(newValue)
  }

  return (
    <div className={cn("space-y-2", className)}>
      {title && <h3 className="text-lg font-semibold mb-2">{title}</h3>}
      <div className="grid grid-cols-3 gap-2">
        {options.map((option) => (
          <Button
            key={option.value}
            onClick={() => handleSelectionChange(option.value)}
            variant={selectedValue === option.value ? "default" : "outline"}
            disabled={disabled}
            className={cn(
              "flex items-center justify-center p-0 m-0 rounded-full transition-all duration-200",
              selectedValue === option.value
                ? "bg-primary text-primary-foreground shadow-lg"
                : "bg-background hover:bg-secondary",
              disabled && "opacity-50 cursor-not-allowed",
              buttonClassName
            )}
          >
            {option.label}
          </Button>
        ))}
      </div>
    </div>
  )
}

"use client"

import React from "react"
import Image from "next/image"
import { ChevronLeft, ChevronRight } from "lucide-react"
import { Carousel } from "react-responsive-carousel"

import "react-responsive-carousel/lib/styles/carousel.min.css"

type ProductImagesProps = {
  productImages: string[]
}

const ProductImages: React.FC<ProductImagesProps> = ({ productImages }) => {
  const [currentIndex, setCurrentIndex] = React.useState(0)
  const handleSlideChange = (index: number) => {
    setCurrentIndex(index)
  }

  return (
    <div className="relative">
      <Carousel
        showThumbs={true}
        showStatus={false}
        useKeyboardArrows={true}
        infiniteLoop={false}
        autoPlay={false}
        selectedItem={currentIndex}
        onChange={handleSlideChange}
        className="product-carousel"
        thumbWidth={60}
        renderThumbs={() =>
          productImages.map((img, index) => (
            <div key={index} className="thumbnail-wrapper h-12 sm:h-14 md:h-16">
              <Image
                src={img}
                alt={`Thumbnail ${index + 1}`}
                width={80}
                height={80}
                className="object-cover rounded-sm thumbnail-image"
              />
            </div>
          ))
        }
        renderArrowPrev={(onClickHandler, hasPrev, label) =>
          hasPrev && (
            <button
              onClick={onClickHandler}
              title={label}
              className="absolute z-[2] w-8 h-8 sm:w-10 sm:h-10 left-1 sm:left-2 rounded-full bg-gray-50 dark:bg-gray-950 cursor-pointer border-none shadow-md flex items-center justify-center"
              style={{
                top: "calc(50% - 20px)",
              }}
            >
              <ChevronLeft className="w-4 h-4 sm:w-5 sm:h-5" />
            </button>
          )
        }
        renderArrowNext={(onClickHandler, hasNext, label) =>
          hasNext && (
            <button
              onClick={onClickHandler}
              title={label}
              className="absolute z-[2] w-8 h-8 sm:w-10 sm:h-10 right-1 sm:right-2 rounded-full bg-gray-50 dark:bg-gray-950 cursor-pointer border-none shadow-md flex items-center justify-center"
              style={{
                top: "calc(50% - 20px)",
              }}
            >
              <ChevronRight className="w-4 h-4 sm:w-5 sm:h-5" />
            </button>
          )
        }
      >
        {productImages.map((img, index) => (
          <div
            key={index}
            className="w-full aspect-square flex items-center justify-center"
          >
            <Image
              src={img}
              alt={`Product image ${index + 1}`}
              width={800}
              height={800}
              priority={index === 0}
              quality={90}
              className="max-h-[60vh] mx-auto rounded-lg"
            />
          </div>
        ))}
      </Carousel>
      <style jsx global>{`
        .carousel .thumbs-wrapper {
          margin: 10px 0 !important;
          overflow: hidden;
        }

        .carousel .thumbs {
          padding: 0 !important;
          display: flex;
          justify-content: center;
          gap: 8px;
        }

        /* Better spacing on mobile */
        @media (max-width: 640px) {
          .carousel .thumbs {
            gap: 4px;
          }

          .carousel .thumb {
            margin-right: 0 !important;
          }
        }

        /* Make sure main image container is responsive */
        .carousel .slide img {
          max-width: 100%;
          max-height: 100%;
        }
      `}</style>
    </div>
  )
}

export default ProductImages

"use client"

import { useEffect, useState } from "react"
import { Droplets } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip"

export type ProductIP = "IP20" | "IP44" | "IP54" | "IP65" | "IP68"

const PRODUCT_IP_OPTIONS_MAP: Record<
  ProductIP,
  { label: string; description: string; increaseOnPricePercent: number }
> = {
  IP20: {
    label: "IP 20",
    description: "Protected against solid objects over 12mm",
    increaseOnPricePercent: 0,
  },
  IP44: {
    label: "IP 44",
    description: "Protected against water splashes from all directions",
    increaseOnPricePercent: 0.04,
  },
  IP54: {
    label: "IP 54",
    description: "Protected against dust and water splashes",
    increaseOnPricePercent: 0.06,
  },
  IP65: {
    label: "IP 65",
    description: "Dust tight and protected against water jets",
    increaseOnPricePercent: 0.08,
  },
  IP68: {
    label: "IP 68",
    description: "Dust tight and protected against long periods of immersion",
    increaseOnPricePercent: 0.1,
  },
}

interface ProductIPSelectorProps {
  value: ProductIP
  onValueChange: (newValue: ProductIP, newPrice?: number) => void
  title?: string
  basePrice: number
  disabled?: boolean
}

export function ProductIPSelector({
  value,
  onValueChange,
  title = "Water Resistance (IP Rating)",
  disabled = false,
  basePrice,
}: ProductIPSelectorProps) {
  const [selectedIp, setSelectedIp] = useState<ProductIP>(value)

  useEffect(() => {
    setSelectedIp(value)
  }, [value])

  const handleIpChange = (newIp: ProductIP) => {
    if (disabled) return
    setSelectedIp(newIp)

    const { increaseOnPricePercent } = PRODUCT_IP_OPTIONS_MAP[newIp]
    const additionalPrice = basePrice * increaseOnPricePercent
    onValueChange(newIp, additionalPrice)
  }

  return (
    <div className="space-y-2">
      {title && <h3 className="text-lg font-semibold mb-2">{title}</h3>}
      <div className="grid sm:grid-cols-3 grid-cols-1 gap-2">
        {Object.entries(PRODUCT_IP_OPTIONS_MAP).map(
          ([ip, { label, description }]) => (
            <TooltipProvider key={ip}>
              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    onClick={() => handleIpChange(ip as ProductIP)}
                    variant={selectedIp === ip ? "default" : "outline"}
                    disabled={disabled}
                    className={cn(
                      "flex items-center justify-center w-full rounded-full",
                      "transition-colors duration-150 ease-in-out",
                      selectedIp === ip
                        ? "bg-primary text-primary-foreground"
                        : "bg-background hover:bg-secondary text-secondary-foreground",
                      disabled && "opacity-50 cursor-not-allowed"
                    )}
                  >
                    <Droplets className="w-4 h-4 mr-1 flex-shrink-0" />
                    <span className="ml-1 truncate">{label}</span>
                  </Button>
                </TooltipTrigger>
                <TooltipContent className="sm:block hidden font-medium">
                  {description}
                </TooltipContent>
              </Tooltip>
            </TooltipProvider>
          )
        )}
      </div>
    </div>
  )
}

