interface SidebarItemProps {
  icon: React.ReactNode
  label: string
  isActive?: boolean
  href: string
}

const SidebarItem = ({
  icon,
  label,
  isActive = false,
  href,
}: SidebarItemProps) => {
  return (
    <Link
      href={href}
      className={cn(
        "flex items-center gap-3 px-4 py-2 rounded-md transition-colors",
        isActive
          ? "bg-primary text-primary-foreground"
          : "hover:bg-accent text-muted-foreground"
      )}
