"use client"

import { Button } from "@/components/ui/button"
import { motion } from "framer-motion"
import type React from "react"
import { useState } from "react"

interface CheckmarkProps {
    size?: number
    strokeWidth?: number
    className?: string
    color?: string
}

const draw = {
    hidden: { pathLength: 0, opacity: 0 },
    visible: (i: number) => ({
        pathLength: 1,
        opacity: 1,
        transition: {
            pathLength: {
