                className
            )}
            {...props}
        />
    )
}

const fieldVariants = cva(
    "group/field flex w-full gap-3 data-[invalid=true]:text-destructive",
    {
        variants: {
            orientation: {
                vertical: ["flex-col [&>*]:w-full [&>.sr-only]:w-auto"],
                horizontal: [
                    "flex-row items-center",
                    "[&>[data-slot=field-label]]:flex-auto",
                    "has-[>[data-slot=field-content]]:items-start has-[>[data-slot=field-content]]:[&>[role=checkbox],[role=radio]]:mt-px",
                ],
                responsive: [
                    "flex-col [&>*]:w-full [&>.sr-only]:w-auto @md/field-group:flex-row @md/field-group:items-center @md/field-group:[&>*]:w-auto",
                    "@md/field-group:[&>[data-slot=field-label]]:flex-auto",
                    "@md/field-group:has-[>[data-slot=field-content]]:items-start @md/field-group:has-[>[data-slot=field-content]]:[&>[role=checkbox],[role=radio]]:mt-px",
                ],
            },
        },
        defaultVariants: {
            orientation: "vertical",
        },
    }
)

function Field({
    className,
    orientation = "vertical",
    ...props
}: React.ComponentProps<"div"> & VariantProps<typeof fieldVariants>) {
    return (
        <div
            role="group"
            data-slot="field"
            data-orientation={orientation}
            className={cn(fieldVariants({ orientation }), className)}
            {...props}
        />
    )
}

function FieldContent({ className, ...props }: React.ComponentProps<"div">) {
    return (
        <div
            data-slot="field-content"
            className={cn(
                "group/field-content flex flex-1 flex-col gap-1.5 leading-snug",
                className
            )}
            {...props}
        />
    )
}

function FieldLabel({
    className,
    ...props
}: React.ComponentProps<typeof Label>) {
    return (
        <Label
            data-slot="field-label"
            className={cn(
                "group/field-label peer/field-label flex w-fit gap-2 leading-snug group-data-[disabled=true]/field:opacity-50",
                "has-[>[data-slot=field]]:w-full has-[>[data-slot=field]]:flex-col has-[>[data-slot=field]]:rounded-md has-[>[data-slot=field]]:border [&>*]:data-[slot=field]:p-4",
                "has-data-[state=checked]:bg-primary/5 has-data-[state=checked]:border-primary dark:has-data-[state=checked]:bg-primary/10",
                className
            )}
            {...props}
        />
    )
}

function FieldTitle({ className, ...props }: React.ComponentProps<"div">) {
    return (
        <div
            data-slot="field-label"
            className={cn(
                "flex w-fit items-center gap-2 text-sm leading-snug font-medium group-data-[disabled=true]/field:opacity-50",
                className
            )}
            {...props}
        />
    )
}

function FieldDescription({ className, ...props }: React.ComponentProps<"p">) {
    return (
        <p
            data-slot="field-description"
            className={cn(
                "text-muted-foreground text-sm leading-normal font-normal group-has-[[data-orientation=horizontal]]/field:text-balance",
                "last:mt-0 nth-last-2:-mt-1 [[data-variant=legend]+&]:-mt-1.5",
                "[&>a:hover]:text-primary [&>a]:underline [&>a]:underline-offset-4",
                className
            )}
            {...props}
        />
    )
}

function FieldSeparator({
    children,
    className,
    ...props
}: React.ComponentProps<"div"> & {
    children?: React.ReactNode
}) {
    return (
        <div
            data-slot="field-separator"
            data-content={!!children}
            className={cn(
                "relative -my-2 h-5 text-sm group-data-[variant=outline]/field-group:-mb-2",
                className
            )}
            {...props}
        >
            <Separator className="absolute inset-0 top-1/2" />
            {children && (
                <span
                    className="bg-background text-muted-foreground relative mx-auto block w-fit px-2"
                    data-slot="field-separator-content"
                >
                    {children}
                </span>
            )}
        </div>
    )
}

function FieldError({
    className,
    children,
    errors,
    ...props
}: React.ComponentProps<"div"> & {
    errors?: Array<{ message?: string } | undefined>
}) {
    const content = useMemo(() => {
        if (children) {
            return children
        }

        if (!errors?.length) {
            return null
        }

        const uniqueErrors = [
            ...new Map(errors.map((error) => [error?.message, error])).values(),
        ]

        if (uniqueErrors?.length == 1) {
            return uniqueErrors[0]?.message
        }

        return (
            <ul className="ml-4 flex list-disc flex-col gap-1">
                {uniqueErrors.map(
                    (error, index) =>
                        error?.message && <li key={index}>{error.message}</li>
                )}
            </ul>
        )
    }, [children, errors])

    if (!content) {
        return null
    }

    return (
        <div
            role="alert"
            data-slot="field-error"
            className={cn("text-destructive text-sm font-normal", className)}
            {...props}
        >
            {content}
        </div>
    )
}

export {
    Field, FieldContent, FieldDescription,
    FieldError,
    FieldGroup, FieldLabel, FieldLegend,
    FieldSeparator,
    FieldSet, FieldTitle
}

@/registry/ui/FolderTreeComponent.content:
"use client"

import { cn } from "@/lib/utils"
import { ChevronRight, File, Folder, FolderOpen } from "lucide-react"
import * as React from "react"

interface FileItem {
  id: string
  name: string
  type: "file" | "folder"
  path?: string
  items?: FileItem[]
}

interface FileExplorerNavProps {
  items: FileItem[]
}

export function FolderTreeComponent({ items }: FileExplorerNavProps) {
  const [openFolders, setOpenFolders] = React.useState<Record<string, boolean>>(
    {}
  )

  const toggleFolder = (id: string) => {
    setOpenFolders((prev) => ({ ...prev, [id]: !prev[id] }))
  }

  const renderItem = (item: FileItem, level = 0) => {
    const isOpen = openFolders[item.id]
    const hasItems = item.items && item.items.length > 0

    return (
      <div key={item.id} className="relative">
        <button
          onClick={(e) => {
            e.stopPropagation()
            if (hasItems) toggleFolder(item.id)
          }}
          className={cn(
            "flex w-full items-center gap-2 rounded-md px-2 py-1.5 text-sm text-gray-400 transition-colors hover:bg-white/5",
            isOpen && "text-white"
          )}
          style={{ paddingLeft: `${(level + 1) * 12}px` }}
        >
          {hasItems ? (
            <span className="flex items-center gap-2">
              <ChevronRight
                className={cn(
                  "h-3.5 w-3.5 transition-transform",
                  isOpen && "rotate-90"
                )}
              />
              {isOpen ? (
                <FolderOpen className="h-4 w-4" />
              ) : (
                <Folder className="h-4 w-4" />
              )}
            </span>
          ) : (
            <File className="ml-5.5 h-4 w-4" />
          )}
          <span>{item.name}</span>
          {item.path && (
            <span className="ml-auto text-xs text-gray-500">{item.path}</span>
          )}
        </button>
        {hasItems && isOpen && (
          <div className="relative">
            {item.items!.map((subItem) => renderItem(subItem, level + 1))}
          </div>
        )}
      </div>
    )
  }

  return (
    <div className="sm:w-72 w-60 overflow-hidden rounded-lg bg-gray-950 p-2 ">
      <div className="space-y-0.5">{items.map((item) => renderItem(item))}</div>
    </div>
  )
}

@/registry/ui/FolderTreeMenu.content:
"use client"

import {
  Sidebar,
  SidebarContent,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSub,
  SidebarMenuSubButton,
  SidebarMenuSubItem,
} from "@/components/ui/sidebar"
import { useSubmenuState } from "@/hooks/use-submenu-state"
import { cn } from "@/lib/utils"
import { ChevronDown, ChevronRight } from "lucide-react"

interface MenuItem {
  id: string
  title: string
  items?: MenuItem[]
}

interface CardSidenavProps {
  items: MenuItem[]
}

export function FolderTreeMenu({ items }: CardSidenavProps) {
  const { openStates, toggleSubmenu } = useSubmenuState()

  const renderMenuItem = (item: MenuItem) => {
    const hasSubItems = item.items && item.items.length > 0
    const isOpen = openStates[item.id]

    return (
      <SidebarMenuItem key={item.id}>
        <SidebarMenuButton
          onClick={() => hasSubItems && toggleSubmenu(item.id)}
          className={cn(
            "w-full justify-between",
            hasSubItems && "font-semibold"
          )}
        >
          {item.title}
          {hasSubItems && (
            <span className="ml-auto">
              {isOpen ? (
                <ChevronDown className="h-4 w-4" />
              ) : (
                <ChevronRight className="h-4 w-4" />
              )}
            </span>
          )}
        </SidebarMenuButton>
        {hasSubItems && (
          <SidebarMenuSub>
            {isOpen &&
              item.items!.map((subItem) => (
                <SidebarMenuSubItem key={subItem.id}>
                  <SidebarMenuSubButton>{subItem.title}</SidebarMenuSubButton>
                </SidebarMenuSubItem>
              ))}
          </SidebarMenuSub>
        )}
      </SidebarMenuItem>
    )
  }

  return (
    <Sidebar className="w-64 rounded-xl border bg-card text-card-foreground shadow-sm">
      <SidebarHeader className="px-4 py-2">
        <h2 className="text-lg font-semibold">Menu</h2>
      </SidebarHeader>
      <SidebarContent>
        <SidebarMenu>{items.map(renderMenuItem)}</SidebarMenu>
      </SidebarContent>
    </Sidebar>
  )
}

