@/registry/ui/note.content:
"use client"

import { cn } from "@/lib/utils"
import { AnimatePresence, motion } from "framer-motion"
import { AlertTriangle, CheckCircle2, Info, X, XCircle } from "lucide-react"
import { JSX, useState } from "react"

type NoteVariant = "info" | "warning" | "success" | "error" | "neutral"

interface NoteProps {
  variant?: NoteVariant
  title?: string
  children: React.ReactNode
  closable?: boolean
  onClose?: () => void
  className?: string
}

const variantStyles: Record<
  NoteVariant,
  { border: string; text: string; icon: JSX.Element }
> = {
  info: {
    border: "border-blue-200 dark:border-blue-800",
    text: "text-blue-600 dark:text-blue-400",
    icon: <Info className="w-4 h-4" />,
  },
  warning: {
    border: "border-amber-200 dark:border-amber-800",
    text: "text-amber-600 dark:text-amber-400",
    icon: <AlertTriangle className="w-4 h-4" />,
  },
  success: {
    border: "border-green-200 dark:border-green-800",
    text: "text-green-600 dark:text-green-400",
    icon: <CheckCircle2 className="w-4 h-4" />,
  },
  error: {
    border: "border-red-600 dark:border-red-800",
    text: "text-red-600 dark:text-red-400",
    icon: <XCircle className="w-4 h-4" />,
  },
  neutral: {
    border: "border-gray-200 dark:border-gray-700",
    text: "text-gray-600 dark:text-gray-400",
    icon: <Info className="w-4 h-4" />,
  },
}

export function Note({
  variant = "neutral",
  title,
  children,
  closable,
  onClose,
  className,
}: NoteProps) {
  const [visible, setVisible] = useState(true)
  const style = variantStyles[variant]

  const handleClose = () => {
    setVisible(false)
    onClose?.()
  }

  return (
    <AnimatePresence>
      {visible && (
        <motion.div
          initial={{ opacity: 0, scale: 0.98 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.98 }}
          transition={{ duration: 0.2 }}
          className={cn(
            "relative flex items-start gap-2 rounded-xl border-2 p-3 bg-white/80 dark:bg-zinc-900/50 backdrop-blur-sm",
            style.border,
            className
          )}
        >
          <div className={cn("mt-0.5", style.text)}>{style.icon}</div>

          <div className="flex-1 space-y-1">
            {title && (
              <h4 className="text-sm font-medium text-black dark:text-white">
                {title}
              </h4>
            )}
            <div className="text-sm text-zinc-700 dark:text-zinc-300">
              {children}
            </div>
          </div>

          {closable && (
            <button
              onClick={handleClose}
              className="p-1 rounded-md text-zinc-400 hover:text-zinc-600 dark:hover:text-zinc-200 transition"
            >
              <X className="w-4 h-4" />
            </button>
          )}
        </motion.div>
      )}
    </AnimatePresence>
  )
}

@/registry/ui/pagination.content:
import { cn } from "@/lib/utils"
import {
  ChevronLeftIcon,
  ChevronRightIcon,
  MoreHorizontalIcon,
} from "lucide-react"
import Link from "next/link"
import * as React from "react"

function Pagination({ className, ...props }: React.ComponentProps<"nav">) {
  return (
    <nav
      role="navigation"
      aria-label="pagination"
      data-slot="pagination"
      className={cn("mx-auto flex w-full justify-center", className)}
      {...props}
    />
  )
}

function PaginationContent({
  className,
  ...props
}: React.ComponentProps<"ul">) {
  return (
    <ul
      data-slot="pagination-content"
      className={cn(
        "flex flex-row items-center gap-1 rounded-2xl bg-background/80 backdrop-blur-md border border-border/50 p-1 shadow-sm transition-all",
        className
      )}
      {...props}
    />
  )
}

function PaginationItem({ ...props }: React.ComponentProps<"li">) {
  return <li data-slot="pagination-item" {...props} />
}

type PaginationLinkProps = {
  href?: string
  className?: string
  isActive?: boolean
  size?: "default" | "sm" | "lg" | "icon"
} & (
  | ({ href: string } & Omit<React.ComponentProps<typeof Link>, "className">)
  | ({ href?: never } & React.ComponentProps<"button">)
)

function PaginationLink({
  className,
  isActive,
  size = "icon",
  ...props
}: PaginationLinkProps) {
  const classes = cn(
    // Base styles - Apple inspired
    "relative inline-flex items-center justify-center",
    "font-medium text-sm tracking-tight",
    "transition-all duration-200 ease-out",
    "focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring/50 focus-visible:ring-offset-2",
    
    // Size variants
    size === "icon" && "h-8 w-8 rounded-xl",
    size === "sm" && "h-7 px-3 rounded-xl",
    size === "default" && "h-8 px-3.5 rounded-xl",
    size === "lg" && "h-9 px-4 rounded-xl",
    
    // Active state - Apple blue accent
    isActive && [
      "bg-primary text-primary-foreground",
      "shadow-sm shadow-primary/20",
      "scale-100",
    ],
    
    // Inactive state - subtle hover
    !isActive && [
      "text-muted-foreground hover:text-foreground",
      "hover:bg-accent/50",
      "active:scale-95",
    ],
    
    className
  )

  const commonProps = {
    "aria-current": isActive ? ("page" as const) : undefined,
    "data-slot": "pagination-link" as const,
    "data-active": isActive,
    className: classes,
  }

  if ("href" in props && props.href) {
    const { href, ...linkProps } = props
    return <Link href={href} {...commonProps} {...linkProps} />
  }

  const buttonProps = props as React.ComponentProps<"button">
  return <button type="button" {...commonProps} {...buttonProps} />
}

function PaginationPrevious({
  className,
  ...props
}: Omit<PaginationLinkProps, "size">) {
  return (
    <PaginationLink
      aria-label="Go to previous page"
      size="default"
      className={cn(
        "gap-1.5 pl-2.5 pr-3 font-medium",
        "hover:bg-accent/60 hover:shadow-sm",
        className
      )}
      {...(props as PaginationLinkProps)}
    >
      <ChevronLeftIcon className="h-4 w-4" strokeWidth={2.5} />
      <span className="hidden sm:inline-block">Previous</span>
    </PaginationLink>
  )
}

function PaginationNext({
  className,
  ...props
}: Omit<PaginationLinkProps, "size">) {
  return (
    <PaginationLink
      aria-label="Go to next page"
      size="default"
      className={cn(
        "gap-1.5 pl-3 pr-2.5 font-medium",
        "hover:bg-accent/60 hover:shadow-sm",
        className
      )}
      {...(props as PaginationLinkProps)}
    >
      <span className="hidden sm:inline-block">Next</span>
      <ChevronRightIcon className="h-4 w-4" strokeWidth={2.5} />
    </PaginationLink>
  )
}

function PaginationEllipsis({
  className,
  ...props
}: React.ComponentProps<"span">) {
  return (
    <span
      aria-hidden
      data-slot="pagination-ellipsis"
      className={cn(
        "flex h-8 w-8 items-center justify-center",
        "text-muted-foreground/50",
        className
      )}
      {...props}
    >
      <MoreHorizontalIcon className="h-4 w-4" strokeWidth={2.5} />
      <span className="sr-only">More pages</span>
    </span>
  )
}

export {
  Pagination,
  PaginationContent,
  PaginationEllipsis,
  PaginationItem,
  PaginationLink,
  PaginationNext,
  PaginationPrevious
}

