<Pre className="language-typescript" folderPath="components/FolderExample.tsx">
{`"use client"

import { FolderTree } from "./FolderTree"

type FileItem = {
  id: string
  name: string
  type: "file" | "folder"
  path?: string
  items?: FileItem[]
}

const fileStructure: FileItem[] = [
  {
    id: "app",
    name: "app",
    type: "folder",
    items: [
      {
        id: "layout",
        name: "layout.js",
        type: "file",
      },
      {
        id: "marketing",
        name: "(marketing)",
        type: "folder",
        items: [
          {
            id: "about",
            name: "about",
            type: "folder",
            path: "/about",
            items: [
              {
                id: "about-page",
                name: "page.js",
                type: "file",
              },
            ],
          },
        ],
      },
    ],
  },
]

export default function FolderTreeExample() {
  return (
    <div className="flex items-center justify-center">
      <FolderTree items={fileStructure} />
    </div>
  )
}
`}
</Pre>
</CodeBlockWrapper>
