@/registry/ui/step.content:
"use client"

import { cn, convertToArabicNumerals } from "@/lib/utils"
import type { StepItemProps, StepProps } from "@/types/components"
import { useLocale } from "next-intl"
import { Children, type PropsWithChildren } from "react"

export function Step({ children, className }: PropsWithChildren<StepProps>) {
    const length = Children.count(children)
    const locale = useLocale()

    return (
        <div className={cn("flex flex-col w-full relative", className)}>
            {Children.map(children, (child, index) => (
                <div className="relative ltr:pl-10 rtl:pr-10">
                    {index < length - 1 && (
                        <span className="absolute ltr:left-4 rtl:right-4 top-8 w-px h-full bg-zinc-200 dark:bg-zinc-700" />
                    )}
                    <span className="absolute ltr:left-0 rtl:right-0 top-1 w-8 h-8 flex items-center justify-center rounded-full bg-zinc-100 dark:bg-zinc-800 text-zinc-800 dark:text-zinc-200 text-sm font-medium">
                        {locale === "ar" ? convertToArabicNumerals(index + 1) : (index + 1).toString()}
                    </span>
                    <div className="pb-8">{child}</div>
                </div>
            ))}
        </div>
    )
}

export function StepItem({ children, title, className }: StepItemProps) {
    return (
        <div className={cn("space-y-3", className)}>
            {title && <h3 className="text-base md:text-lg font-medium text-zinc-900 dark:text-zinc-50">{title}</h3>}
            <div className="text-sm md:text-base text-zinc-600 dark:text-zinc-300 leading-relaxed !-mt-0">
                <div className="space-y-2">{children}</div>
            </div>
        </div>
    )
}

@/registry/ui/switch.content:
"use client";

import { Label } from "@/components/ui/label";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";
import * as SwitchPrimitive from "@radix-ui/react-switch";
import * as React from "react";

interface SwitchProps extends React.ComponentProps<typeof SwitchPrimitive.Root> {
  size?: "sm" | "md" | "lg";
  label?: string;
  tooltip?: string;
}

const sizeConfig = {
  sm: {
    track: "h-6 w-10 p-1",
    thumb: "h-4 w-4",
    translate: "data-[state=checked]:translate-x-[1rem]",
    text: "text-sm",
  },
  md: {
    track: "h-7 w-12 p-1",
    thumb: "h-5 w-5",
    translate: "data-[state=checked]:translate-x-[1.25rem]",
    text: "text-base",
  },
  lg: {
    track: "h-8 w-14 p-1",
    thumb: "h-6 w-6",
    translate: "data-[state=checked]:translate-x-[1.5rem]",
    text: "text-lg",
  },
};

export const Switch: React.FC<SwitchProps> = ({
  size = "md",
  label,
  tooltip,
  className,
  ...props
}) => {
  const id = React.useId();
  const [showTooltip, setShowTooltip] = React.useState(false);
  const timeoutRef = React.useRef<NodeJS.Timeout | null>(null);

  const config = sizeConfig[size];

  const handleMouseEnter = () => {
    if (!tooltip) return;
    timeoutRef.current = setTimeout(() => setShowTooltip(true), 2000);
  };
  const handleMouseLeave = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setShowTooltip(false);
  };

  React.useEffect(() => {
    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, []);

  return (
    <div className={cn("inline-flex items-center gap-2", className)}>
      <TooltipProvider>
        {tooltip ? (
          <Tooltip open={showTooltip} onOpenChange={setShowTooltip}>
            <TooltipTrigger asChild>
              <SwitchPrimitive.Root
                id={id}
                className={cn(
                  "relative inline-flex items-center rounded-full transition-all duration-300 ease-out focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2",
                  config.track,
                  props.disabled
                    ? "bg-gray-200 dark:bg-gray-700 cursor-not-allowed opacity-60"
                    : "bg-gray-300 dark:bg-gray-600 cursor-pointer",
                  "data-[state=checked]:bg-black dark:data-[state=checked]:bg-white"
                )}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                {...props}
              >
                <SwitchPrimitive.Thumb
                  className={cn(
                    "block rounded-full bg-white dark:bg-black shadow-lg transition-all duration-300 ease-[cubic-bezier(0.25,0.46,0.45,0.94)]",
                    config.thumb,
                    config.translate,
                    props.disabled ? "" : "hover:scale-105"
                  )}
                />
              </SwitchPrimitive.Root>
            </TooltipTrigger>
            <TooltipContent
              side="top"
              align="center"
              className="bg-gray-900 text-white text-xs py-1 px-2 rounded-md shadow-lg"
            >
              {tooltip}
            </TooltipContent>
          </Tooltip>
        ) : (
          <SwitchPrimitive.Root
            id={id}
            className={cn(
              "relative inline-flex items-center rounded-full transition-all duration-300 ease-out focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2",
              config.track,
              props.disabled
                ? "bg-gray-200 dark:bg-gray-700 cursor-not-allowed opacity-60"
                : "bg-gray-300 dark:bg-gray-600 cursor-pointer",
              "data-[state=checked]:bg-black dark:data-[state=checked]:bg-white"
            )}
            {...props}
          >
            <SwitchPrimitive.Thumb
              className={cn(
                "block rounded-full bg-white dark:bg-black shadow-lg transition-transform duration-300 ease-[cubic-bezier(0.175,0.885,0.32,1.275)]",
                config.thumb,
                config.translate,
                props.disabled ? "" : "hover:scale-105"
              )}
            />
          </SwitchPrimitive.Root>
        )}
      </TooltipProvider>
      {label && (
        <Label
          htmlFor={id}
          className={cn(
            config.text,
            props.disabled ? "opacity-60 cursor-not-allowed" : "cursor-pointer"
          )}
          onClick={() => {
            if (!props.disabled) {
              props.onCheckedChange?.(!props.checked);
            }
          }}
        >
          {label}
        </Label>
      )}
    </div>
  );
};

